QTableWidget {
    background-color: #FFFFFF;
    border: 1px solid #D4C8FB;
    gridline-color: #E5DDFF;
    color: #463B6B;
    alternate-background-color: #F5F1FF;
    selection-background-color: #D6C9FF;
}

QHeaderView::section {
    background-color: #E3DAFF;
    color: #3A2F55;
    font-weight: bold;
}
